import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router'
import AuthorForm from './AuthorForm'
import axios from 'axios';

export default function AuthorUpdateForm(props) {
    const {_id} = useParams();
    const [name, setName] = useState("");

    useEffect(() => {
        let isMounted = true;
        console.log(`http://localhost:8000/api/authors/${_id}`);
        axios.get(`http://localhost:8000/api/authors/${_id}`)
        .then(response => {
            if(isMounted){ 
                setName(response.data.name)
            }
        })
        .catch(err => console.log("Error: ", err))

        return () => {isMounted = false};
    }, [])


    function updateAuthor(e, author){
        e.preventDefault();
        axios.put(`http://localhost:8000/api/authors/update/${_id}`, author)
            .then(res => console.log("Response: ", res))
            .catch(err => {
                console.log(err.response.data.errors);
                const errorResponse = err.response.data.errors; //Get the errors from err.response.data
                const errorArr = [];
                for(const key of Object.keys(errorResponse)){
                    errorArr.push(errorResponse[key].message);
                }
                setErrors(errorArr);
            })
    }

    return (
        <div>
            <AuthorForm submitFunction={updateAuthor} initialName={name}/>
        </div>
    )
}
