import React, { useEffect, useState } from 'react'

export default function AuthorForm({initialName, submitFunction}) {
    const [name, setName] = useState(initialName);

    //create arraya to store errors from the API
    const [errors, setErrors] = useState([]);

    useEffect(() => {
        setName(initialName);

    }, [initialName])

    return (
        <form onSubmit={(e) => submitFunction(e, {name: name})}>
            <div className="form-group">
            <label htmlFor="name">Author Name</label>
            <p>{name}</p>
            <input type="text" className="form-control" value={name} onChange={e => setName(e.target.value)} id="name"/>
            </div>

            <button type="submit" className="btn btn-primary mt-3">Submit</button>
        </form>
    )
}
